require 'codesignal/message_from_binary_code'

describe 'Simple tests' do
  it '#binary_message' do
    expect(binary_message('010010000110010101101100011011000110111100100001')).to eq('Hello!')
    expect(binary_message('01001101011000010111100100100000011101000110100001100101001000000100011001101111011100100110001101100101001000000110001001100101001000000111011101101001011101000110100000100000011110010110111101110101')).to eq("May the Force be with you")
    expect(binary_message('010110010110111101110101001000000110100001100001011001000010000001101101011001010010000001100001011101000010000001100000011010000110010101101100011011000110111100101110')).to eq("You had me at `hello.")
    expect(binary_message('010001010110110001100101011011010110010101101110011101000110000101110010011110010010110000100000011011010111100100100000011001000110010101100001011100100010000001010111011000010111010001110011011011110110111000100001')).to eq("Elementary, my dear Watson!")
    expect(binary_message('subject010011000110111101110110011001010010000001000110011011110111001000100000010000010110110001101100001011000010000001001000011000010111010001110010011001010110010000100000010001100110111101110010001000000100111001101111011011100110010100101110')).to eq("Love For All, Hatred For None.")
    expect(binary_message('0100001101101000011000010110111001100111011001010010000001110100011010000110010100100000011101110110111101110010011011000110010000100000011000100111100100100000011000100110010101101001011011100110011100100000011110010110111101110101011100100111001101100101011011000110011000101110')).to eq("Change the world by being yourself.")
    expect(binary_message('01000101011101100110010101110010011110010010000001101101011011110110110101100101011011100111010000100000011010010111001100100000011000010010000001100110011100100110010101110011011010000010000001100010011001010110011101101001011011100110111001101001011011100110011100101110')).to eq("Every moment is a fresh beginning.")
    expect(binary_message('')).to eq("Never regret anything that made you smile.")
    expect(binary_message('010001000110100101100101001000000111011101101001011101000110100000100000011011010110010101101101011011110111001001101001011001010111001100101100001000000110111001101111011101000010000001100100011100100110010101100001011011010111001100101110')).to eq("Die with memories, not dreams.")
    expect(binary_message('0100000101110011011100000110100101110010011001010010000001110100011011110010000001101001011011100111001101110000011010010111001001100101001000000110001001100101011001100110111101110010011001010010000001110111011001010010000001100101011110000111000001101001011100100110010100101110')).to eq("Aspire to inspire before we expire.")
  end

end
